import { useEffect } from 'react';
import { init, viewport, isTMA } from '@tma.js/sdk';

export function useTelegramFullscreen() {
  useEffect(() => {
    async function setupTelegram() {
      if (await isTMA()) {
        await init(); // Инициализация SDK

        // Развертывание на весь экран
        if (viewport.mount.isAvailable()) {
          await viewport.mount();
          viewport.expand(); // Расширение до границ экрана
        }

        // Активация полноэкранного режима
        if (viewport.requestFullscreen.isAvailable()) {
          await viewport.requestFullscreen();
        }
      }
    }
    setupTelegram();
  }, []);
}
