import React, { useState } from 'react';
import { useEffect } from 'react';
import useSound from './useSound';

const SoundTest = () => {
  const [activeSound, setActiveSound] = useState(null);
  const soundOptions = [
    { id: 'kick', label: 'Kick', url: '/sounds/kick.mp3' },
    { id: 'snare', label: 'Snare', url: '/sounds/snare.mp3' },
    { id: 'hihat', label: 'Hi-Hat', url: '/sounds/hihat.mp3' }
  ];

  const handleToggleSound = (soundId) => {
    if (activeSound === soundId) {
      setActiveSound(null);
    } else {
      setActiveSound(soundId);
    }
  };

  return (
    <div>
      <h2>Sound Test</h2>
      <div style={{ display: 'flex', gap: '10px' }}>
        {soundOptions.map(sound => (
          <SoundButton
            key={sound.id}
            sound={sound}
            isActive={activeSound === sound.id}
            onClick={() => handleToggleSound(sound.id)}
          />
        ))}
      </div>
    </div>
  );
};

const SoundButton = ({ sound, isActive, onClick }) => {
  const { play, stop } = useSound(sound.url);

  useEffect(() => {
    if (isActive) {
      play();
    } else {
      stop();
    }
  }, [isActive, play, stop]);

  return (
    <button 
      style={{
        padding: '20px',
        background: isActive ? '#ff5555' : '#dddddd',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer'
      }}
      onClick={onClick}
    >
      {sound.label}
    </button>
  );
};

export default SoundTest;
