import React, { useState } from 'react';
import './InstrumentButton.css';

// Icons for different instrument types
const icons = {
    wave: (
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M2 12h4l3-9 3 18 3-9h7"></path>
        </svg>
    ),
    drum: (
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <ellipse cx="12" cy="16" rx="10" ry="5"></ellipse>
            <path d="M2 12v4"></path>
            <path d="M22 12v4"></path>
            <path d="M12 8v4"></path>
            <ellipse cx="12" cy="8" rx="10" ry="5"></ellipse>
        </svg>
    ),
    star: (
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M12 2l2.5 7h7l-5.5 4 2 7-6-4-6 4 2-7-5.5-4h7z"></path>
        </svg>
    ),
    speaker: (
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <rect x="4" y="6" width="16" height="12" rx="2"></rect>
            <circle cx="12" cy="12" r="3"></circle>
        </svg>
    )
};

const InstrumentButton = ({ type }) => {
    const [isPressed, setIsPressed] = useState(false);
    const handleClick = () => {
        const newState = !isPressed;
        setIsPressed(newState);

        if (newState) {
          console.log('ВКЛ');
        } else {
          console.log('ВЫКЛ');
        }
    };

    return (
        <div 
            className={`instrument-button-container ${isPressed ? 'pressed' : ''}`}
            onClick={handleClick}
        >
            <div className="back-instrument-button"></div>
            <button
                className="instrument-button"
            >
                <div className="instrument-icon">
                    {icons[type]}
                </div>
            </button>
        </div>
    );
};

export default InstrumentButton;
