import React, { useState } from 'react';
import './Button3D.css';

const Button3D = ({ 
  children = 'Нажми меня',
  width = '120px',
  height = '50px',
  depth = '8px',
  mainColor = '#4a6baf',
  shadowColor = '#2d3b5e',
  pressedColor = '#3a5683'
}) => {
  const [isPressed, setIsPressed] = useState(false);

  return (
    <div 
      className="button-3d-container"
      style={{ 
        '--width': width,
        '--height': height,
        '--depth': depth,
        '--main-color': mainColor,
        '--shadow-color': shadowColor,
        '--pressed-color': pressedColor
      }}
    >
      <button
        className={`button-3d ${isPressed ? 'pressed' : ''}`}
        onMouseDown={() => setIsPressed(true)}
        onMouseUp={() => setIsPressed(false)}
        onMouseLeave={() => setIsPressed(false)}
      >
        <span className="button-face">
          {children}
        </span>
        {/* Нижняя грань (тень) */}
        <span className="button-shadow" />
      </button>
    </div>
  );
};

export default Button3D;
