import React from 'react';
import './App.css';
import Cassette from './components/Cassette';
import MelodyButton from './components/MelodyButton';
import InstrumentButton from './components/InstrumentButton';
import RecordButton from './components/RecordButton';
import VolumeControl from './components/VolumeControl';
import PlaybackControls from './components/PlaybackControls';

function App() {
  return (
    <div className="dj-station">
      {/* Cassette component */}
      <Cassette />

      {/* Melody buttons row */}
      <div className="melody-row">
        <MelodyButton />
        <MelodyButton />
        <MelodyButton />
      </div>

      {/* Sound buttons - each row represents a category */}
      <div className="sound-grid">
        {/* Audio wave buttons */}
        <div className="button-row">
          <InstrumentButton type="wave" />
          <InstrumentButton type="wave" />
          <InstrumentButton type="wave" />
        </div>

        {/* Drum buttons */}
        <div className="button-row">
          <InstrumentButton type="drum" />
          <InstrumentButton type="drum" />
          <InstrumentButton type="drum" />
        </div>

        {/* Star buttons */}
        <div className="button-row">
          <InstrumentButton type="star" />
          <InstrumentButton type="star" />
          <InstrumentButton type="star" />
        </div>

        {/* Speaker buttons */}
        <div className="button-row">
          <InstrumentButton type="speaker" />
          <InstrumentButton type="speaker" />
          <InstrumentButton type="speaker" />
        </div>
      </div>

      {/* Record button */}
      <RecordButton />

      {/* Volume slider */}
      <VolumeControl />

      {/* Playback controls */}
      <PlaybackControls />
    </div>
  );
}

export default App;
