import React, { useState } from 'react';
import { useEffect } from 'react';
import useSound from './useSound';

const SoundTest = () => {
  const [activeButton, setActiveButton] = useState(null);
  const buttons = [
    { id: 'kick', label: 'Kick', url: '/sounds/melodies/1.wav' },
    { id: 'snare', label: 'Snare', url: '/sounds/melodies/2.wav' },
    { id: 'hihat', label: 'Hi-Hat', url: '/sounds/melodies/3.wav' }
  ];

  const handleButtonClick = (buttonId) => {
    if (activeButton === buttonId) {
      setActiveButton(null);
    } else {
      setActiveButton(buttonId);
    }
  };

  return (
    <div>
      <h2>Sound Test</h2>
      <div style={{ display: 'flex', gap: '10px' }}>
        {buttons.map(button => (
          <SoundButton
            key={button.id}
            soundUrl={button.url}
            isActive={activeButton === button.id}
            onClick={() => handleButtonClick(button.id)}
          />
        ))}
      </div>
    </div>
  );
};

const SoundButton = ({ soundUrl, isActive, onClick }) => {
  const { play, stop } = useSound(soundUrl);

  useEffect(() => {
    if (isActive) {
      play();
    } else {
      stop();
    }
  }, [isActive, play, stop]);

  return (
    <button 
      style={{
        padding: '20px',
        background: isActive ? '#ff5555' : '#dddddd',
